(()=>{"use strict";$(document).ready((function(){$(document).on("submit","#edit-user-form",(async function(e){const t=window.location.href.split("/"),n=t[t.length-1],o=parseInt(n,10);e.preventDefault(),e.preventDefault();let r=$(this).serialize();await fetch(`/api/user/${o}`,{method:"PUT",headers:{"Content-Type":"application/x-www-form-urlencoded"},body:r}).then((e=>{e.ok?location.href="/user":alert("Произошла ошибка при обновлении данных пользователя.")})).catch((e=>{console.error("Ошибка:",e),alert("Произошла ошибка при обновлении данных пользователя.")}))}))}))})();